def load_icon(b64_data, size=(20, 20), color=None):
    try:
        img_data = base64.b64decode(b64_data)
        image = Image.open(BytesIO(img_data))
        return ctk.CTkImage(light_image=image, dark_image=image, size=size)
    except Exception:
        return None

